
public class Examples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int val1=10;
    int val2=20;
    int result=val1+val2;
    System.out.println("the value of result after additio:"+result);
    System.out.println("the value of val1:"+val1);
    System.out.println("the value value of val2:"+val2);
    
    result=val1-val2;
    
    System.out.println("the value of result after subtracion:"+result);
    
    int newresult;
    newresult=val1/val2;
    System.out.println("the value of newresult:"+newresult);
    
    newresult=val1%val2;
    System.out.println("the value of new result:"+newresult);
     
    newresult=val1*val2;
    System.out.println("the value of new result:"+newresult);
    
    
	}

}
