package com.loopStatements;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;i<6;i++)
		{
			System.out.print(i);
			System.out.println("my mother is my god");
			
		}
		System.out.println("#########");
		
		for(int j=3;j<=7;j++)
		{
			System.out.println("selenium");
			
		}
		System.out.println("***********");
		
	    for(int k=5;k>=1;k--)
	    {
	    	System.out.println("testing");
	    }
	    System.out.println("@@@@@@@@@@@");
	    for(int n=10;n>=1;n--)
	    {
	    	System.out.println(n+"livetech");
	    }
	}

}
